import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './PlayerSelection.css';

const PlayerSelection = () => {
    const [players, setPlayers] = useState([]);
    const [selectedPlayers, setSelectedPlayers] = useState([]);
    const [budget, setBudget] = useState(9000000);
    const [searchQuery, setSearchQuery] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    // Fetch players from backend
    useEffect(() => {
        const fetchPlayers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/players');
                console.log("Fetched Players:", response.data);
                setPlayers(response.data);
            } catch (error) {
                console.error("Error fetching players:", error);
            }
        };
        fetchPlayers();
    }, []);
    

    // Filter players based on search query
    const filteredPlayers = players.filter(player =>
        player.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    // Handle selecting/unselecting a player
    const handleSelectPlayer = (player) => {
        const isSelected = selectedPlayers.find(p => p.id === player.id);

        if (isSelected) {
            // Unselect player
            setSelectedPlayers(selectedPlayers.filter(p => p.id !== player.id));
            setBudget(budget + player.value);
        } else {
            if (selectedPlayers.length >= 11) {
                setErrorMessage("You can only select up to 11 players.");
                return;
            }
            if (budget - player.value < 0) {
                setErrorMessage(`Insufficient budget to select ${player.name}.`);
                return;
            }
            setSelectedPlayers([...selectedPlayers, player]);
            setBudget(budget - player.value);
            setErrorMessage('');
        }
    };

    return (
        <div className="app-container">
            {/* Navbar */}
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
                <div className="container-fluid">
                    <a className="navbar-brand" href="/">SpiritII Fantasy Cricket</a>
                </div>
            </nav>

            {/* Search Box */}
            <div className="hero-section">
                <input
                    type="search"
                    className="form-control hero-search"
                    placeholder="Search Players"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                {searchQuery && (
                    <ul className="search-suggestions">
                        {filteredPlayers.slice(0, 5).map(player => (
                            <li key={player.id} onClick={() => setSearchQuery(player.name)}>
                                {player.name}
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            {/* Budget Display */}
            <div className="budget-display">
                <h2>Remaining Budget</h2>
                <p className="budget-amount">Rs. {budget.toLocaleString()}</p>
            </div>

            {/* Error Message */}
            {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

            {/* Player Cards */}
            <div className="player-cards">
                {filteredPlayers.map((player) => (
                    <div key={player.id} className="player-card">
                        <div className="card">
                            <div className="card-body">
                                <img src={player.image} alt={player.name} className="player-image" />
                                <div className="player-details">
                                    <h3>{player.name}</h3>
                                    <p>{player.university}</p>
                                    <p>Value: Rs. {player.value.toLocaleString()}</p>
                                    <button
                                        className={`btn ${selectedPlayers.find(p => p.id === player.id) ? 'btn-danger' : 'btn-primary'}`}
                                        onClick={() => handleSelectPlayer(player)}
                                    >
                                        {selectedPlayers.find(p => p.id === player.id) ? 'Unselect' : 'Select'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Submit Button */}
            <button 
                className="btn btn-success submit-btn"
                disabled={selectedPlayers.length !== 11}
                onClick={() => console.log("Team Submitted:", selectedPlayers)}
            >
                Submit Team
            </button>
        </div>
    );
};

export default PlayerSelection;
