import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './PlayerSelection.css';

const PlayerSelection = () => {
    const [players, setPlayers] = useState([]);
    const [selectedPlayers, setSelectedPlayers] = useState([]);
    const [budget, setBudget] = useState(9000000);
    const [searchQuery, setSearchQuery] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [showHero, setShowHero] = useState(true);
    const [lastScrollY, setLastScrollY] = useState(0);

    useEffect(() => {
        fetch("http://localhost:5000/players")
            .then(response => response.json())
            .then(data => {
                console.log("Fetched players:", data); // Debugging log
                setPlayers(Array.isArray(data) ? data : []); // Ensure it's an array
            })
            .catch(error => console.error("Error fetching players:", error));
    }, []);
    

    const handleSelectPlayer = (player) => {
        if (selectedPlayers.includes(player)) {
            setErrorMessage(`${player.name} is already selected.`);
            return;
        }
        if (budget - player.value < 0) {
            setErrorMessage(`Insufficient budget to select ${player.name}.`);
            return;
        }
        setSelectedPlayers([...selectedPlayers, player]);
        setBudget(budget - player.value);
        setErrorMessage('');
    };

    const filteredPlayers = players.filter(player =>
        player.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    useEffect(() => {
        const handleScroll = () => {
            const currentScrollY = window.scrollY;
            setShowHero(currentScrollY <= lastScrollY);
            setLastScrollY(currentScrollY);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [lastScrollY]);

    return (
        <div className="app-container">
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
                <div className="container-fluid">
                    <a className="navbar-brand" href="/">SpiritII Fantasy Cricket</a>
                    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                            <li className="nav-item"><a className="nav-link active" href="/">Player Selection</a></li>
                            <li className="nav-item"><a className="nav-link" href="/team">Team Management</a></li>
                            <li className="nav-item"><a className="nav-link" href="/leaderboard">Leaderboard</a></li>
                            <li className="nav-item"><a className="nav-link" href="/chatbot">Chatbot</a></li>
                        </ul>
                        <button className="btn btn-outline-light">Logout</button>
                    </div>
                </div>
            </nav>

            <div className={`hero-section ${showHero ? 'visible' : 'hidden'}`}>
                <div className="hero-content">
                    <h1 className="hero-text">Time to select your team</h1>
                    <div className="hero-search-container">
                        <input type="search" className="form-control hero-search" placeholder="Search Players" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
                    </div>
                </div>
            </div>

            <div className="budget-display">
                <div className="budget-card">
                    <div className="budget-icon">💰</div>
                    <h2>Remaining Budget</h2>
                    <p className="budget-amount">Rs. {budget.toLocaleString()}</p>
                </div>
            </div>

            {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

            <div className="player-cards">
                {filteredPlayers.map((player) => (
                    <div key={player.id} className="player-card">
                        <div className="card">
                            <div className="card-body">
                                <img src={player.image || 'https://via.placeholder.com/80'} alt={player.name} className="player-image" />
                                <div className="player-details">
                                    <h3>{player.name}</h3>
                                    <p>{player.university}</p>
                                    <p>Value: Rs. {player.value.toLocaleString()}</p>
                                    <button className="btn btn-primary" onClick={() => handleSelectPlayer(player)} disabled={selectedPlayers.includes(player) || budget - player.value < 0}>
                                        {selectedPlayers.includes(player) ? 'Selected' : 'Select'}
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PlayerSelection;
